// 取得原始資料
let data = msg.payload;

// 模擬校正：假設原始值 55 對應大約 0 ppm
let ppm = data.mq7 * 0.5;   // 假設比例，需依實際校正修改

// 判斷安全範圍
let status = "正常";
if (ppm > 9 && ppm <= 50) {
    status = "注意：超過 WHO 建議值";
} else if (ppm > 50) {
    status = "警告：超過 OSHA 標準";
}

// 轉換 timestamp 為台北時間
let utcDate = new Date(data.timestamp);
let taipeiTime = utcDate.toLocaleString("zh-TW", {
    timeZone: "Asia/Taipei",
    hour12: false
});

// 加入到輸出物件
msg.payload = {
    mq7_raw: data.mq7,
    ppm_est: ppm,
    temp: data.temp,
    humidity: data.humidity,
    enthalpy: data.enthalpy,
    timestamp: taipeiTime,   // ⚠️ 改成 taipeiTime，不要用 data.timestamp
    status: status
};

return msg;
